import React, { Component } from 'react';
import Layout from '../../../components/Layout';
import { Button, Table, Tab } from 'semantic-ui-react';
import Campaign from '../../../ethereum/campaign';
import { Link } from '../../../routes';

class RequestIndex extends Component {
    state = {}

    static async getInitialProps(props) {
        const { address } = props.query;
        const campaign = Campaign(address);
        const requestsCount = campaign.methods.getRequestsCount().call();

        const requests = await Promise.all(
            Array(requestsCount).fill().map((Element, index) => {
                return campaign.methods.requests(index).call();
            })
        );

        console.log(requests);

        return { address, requests };
    }

    render() {
        return (
            <Layout>
                <h3>Requests</h3>
                <Link to={"/campaigns/" + this.props.address + '/requests/new'}>
                    <Button floated="right" primary>Add request</Button>
                </Link>
                <Table celled>
                    <Table.Header>
                        <Table.Row textAlign="center">
                            <Table.HeaderCell>Id</Table.HeaderCell>
                            <Table.HeaderCell>Description</Table.HeaderCell>
                            <Table.HeaderCell>Amount</Table.HeaderCell>
                            <Table.HeaderCell>Recipient</Table.HeaderCell>
                            <Table.HeaderCell>Approval Count</Table.HeaderCell>
                            <Table.HeaderCell>Approve</Table.HeaderCell>
                            <Table.HeaderCell>Finalize</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>

                    <Table.Body>
                        {this.props.requests.map((request, index) =>

                            <Table.Row textAlign="center">
                                <Table.Cell>{index + 1}</Table.Cell>
                                <Table.Cell>{request.description}</Table.Cell>
                                <Table.Cell>{request.value}</Table.Cell>
                                <Table.Cell>{request.recipient}</Table.Cell>
                                <Table.Cell>{request.approvalCount}</Table.Cell>
                                <Table.Cell>Approve</Table.Cell>
                                <Table.Cell>Finalize</Table.Cell>
                            </Table.Row>
                        )}
                    </Table.Body>
                </Table>
            </Layout>
        );
    }
}

export default RequestIndex;
