import React, { Component } from 'react';
import { Table, Button } from 'semantic-ui-react';
import web3 from '../ethereum/web3';
import Campaign from '../ethereum/campaign';
import { Router } from '../routes';

class RequestRow extends Component {
    state = {
        isLoadingApproval: false
    }

    onApprove = async () => {
        const campaign = Campaign(this.props.address);

        this.setState({ isLoadingApproval: true });

        try {
            //get accounts
            let accounts = await web3.eth.getAccounts();

            //approve
            await campaign.methods.approveRequest(this.props.id - 1).send({
                from: accounts[0]
            })

            Router.replaceRoute("/campaigns/" + this.props.address + "/requests");
        } catch (err) {

        }

        this.setState({ isLoadingApproval: false });

    }

    canFinalize = async () => {
        //Check if current account is that of the manager.
        const campaign = Campaign(this.props.address);

        let accounts = await web3.eth.getAccounts();
        let manager = await campaign.methods.manager().call();

        return accounts[0] === manager ? <Button color="red" basic>Finalize</Button> : <p color="red">Restricted</p>
    }

    render() {
        return (
            <Table.Row textAlign="center">
                <Table.Cell>{this.props.id}</Table.Cell>
                <Table.Cell>{this.props.request.description}</Table.Cell>
                <Table.Cell>{web3.utils.fromWei(this.props.request.value, "ether")}</Table.Cell>
                <Table.Cell>{this.props.request.recipient}</Table.Cell>
                <Table.Cell>{this.props.request.approvalCount} / {this.props.noOfApprovers}</Table.Cell>
                <Table.Cell><Button color="green" basic loading={this.state.isLoadingApproval} onClick={this.onApprove}>Approve</Button></Table.Cell>
                <Table.Cell></Table.Cell>
            </Table.Row>
        );
    }
}

export default RequestRow;
