    state = {
        campaign: {
            balance: '',
            contributors: '',
            minContrib: '',
            pending: ''
        }
    }

    static getInitialProps = async () =>
    {
        let address = this.props.match.params.address;
        console.log(address);
    }

    render() {
        return (
            <Layout>
                <h3>Campaign Details</h3>
                <Grid>
                    <GridColumn width={5}>

                    </GridColumn>
                    <GridColumn width={5}>
                        
                    </GridColumn>
                    <GridColumn width={6}>
                        
                    </GridColumn>
                </Grid>
            </Layout>
        );
    }
}

export default CampaignShow;
