import React, { Component } from 'react';
import Layout from '../../../components/Layout'
import { Input, Form, Grid, Button } from 'semantic-ui-react';

class RequestNew extends Component {
    state = {
        description: '',
        value: '',
        recipient: ''
    }

    static async getInitalProps(props) {
        const { address } = props.query.address;

        return { address };
    }

    onSubmit = async () => {
        
    }

    render() {
        return (
            <Layout>
                <h3>Create a Request</h3>
                <Grid>
                    <Grid.Column width={6}>
                        <Form onSubmit={this.onSubmit}>
                            <Form.Field>
                                <label>Description</label>
                                <Input value={this.state.description} onChange={event => { this.setState({ value: event.target.value }) }} />
                            </Form.Field>
                            <Form.Field>
                                <label>Value</label>
                                <Input
                                    label="Wei"
                                    labelPosition="right"
                                    value={this.state.value}
                                    onChange={event => { this.setState({ value: event.target.value }) }} />
                            </Form.Field>
                            <Form.Field>
                                <label>Recipient</label>
                                <Input value={this.state.recipient} onChange={event => { this.setState({ value: event.target.value }) }} />
                            </Form.Field>
                            <Button primary>Create</Button>
                        </Form>
                    </Grid.Column>
                </Grid>
            </Layout>
        );
    }
}

export default RequestNew;
