import React, { Component } from 'react';
import Layout from '../../../components/Layout'
import { Input, Form, GridColumn } from 'semantic-ui-react';

class RequestNew extends Component {
    state = {
        description: '',
        value: '',
        recipient: ''
    }

    static async getInitalProps(props) {
        const { address } = props.query.address;

        return { address };
    }

    render() {
        return (
            <Layout>
                <h3>Create a Request</h3>
                <GridColumn width={6}>
                    <Form>
                        <Form.Field>
                            <label>Description</label>
                            <Input value={this.state.description} onChange={event => { this.setState({ value: event.target.value }) }} />
                        </Form.Field>
                        <Form.Field>
                            <label>Value</label>
                            <Input value={this.state.value} onChange={event => { this.setState({ value: event.target.value }) }} />
                        </Form.Field>
                        <Form.Field>
                            <label>Recipient</label>
                            <Input value={this.state.recipient} onChange={event => { this.setState({ value: event.target.value }) }} />
                        </Form.Field>
                    </Form>
                </GridColumn>
            </Layout>
        );
    }
}

export default RequestNew;
