import React, { Component } from 'react';
import Layout from '../../../components/Layout'
import { Input, Form, Grid, Button, Message } from 'semantic-ui-react';
import web3 from '../../../ethereum/web3';
import Campaign from '../../../ethereum/campaign';

class RequestNew extends Component {
    state = {
        description: '',
        value: '',
        recipient: '',
        errorMessage: ''
    }

    static async getInitalProps(props) {
        const { address } = props.query.address;

        return { address };
    }

    onSubmit = async () => {
        //Get the campaign contract
        const campaign = Campaign(this.props.address);

        try {
            //Get address
            let accounts = await web3.eth.getAccount();

            //Send request
            await campaign.methods.createRequest(this.state.description, this.state.value, this.state.recipient).send(
                { from: accounts[0] }
            );

        } catch (error) {
            this.setState({ errorMessage: error.message })
        }
    }

    render() {
        return (
            <Layout>
                <h3>Create a Request</h3>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Grid>
                        <Grid.Column width={6}>
                            <Form.Field>
                                <label>Description</label>
                                <Input value={this.state.description} onChange={event => { this.setState({ value: event.target.value }) }} />
                            </Form.Field>
                            <Form.Field>
                                <label>Value</label>
                                <Input
                                    label="Wei"
                                    labelPosition="right"
                                    value={this.state.value}
                                    onChange={event => { this.setState({ value: event.target.value }) }} />
                            </Form.Field>
                            <Form.Field>
                                <label>Recipient</label>
                                <Input value={this.state.recipient} onChange={event => { this.setState({ value: event.target.value }) }} />
                            </Form.Field>
                            <Button primary>Create</Button>
                        </Grid.Column>
                    </Grid>
                    <Message error header="Oops" content={this.state.errorMessage} />
                </Form>
            </Layout>
        );
    }
}

export default RequestNew;
